local LibraryRepository="https://raw.githubusercontent.com/synnyyy/Obsidian/refs/heads/main"local Libraries={Library=LibraryRepository.."/Library.lua",ThemeManager=LibraryRepository.."/addons/ThemeManager.lua",Information="https://raw.githubusercontent.com/Synergy-Networks/products/refs/heads/main/Rift/Assets/Information.lua"}local Total=3;local Completed=0;for Name,Url in next,Libraries do task.spawn(function()local Content=game:HttpGet(Url)Libraries[Name]=loadstring(Content)()Completed=Completed+1 end)end;repeat task.wait()until Completed==Total;Libraries.Library:SetAssetsFolder("RiftAssets")Libraries.Library:SetAssetsUrl("https://raw.githubusercontent.com/Synergy-Networks/products/refs/heads/main/Rift/Assets")Libraries.Library:SetModulesUrl("https://raw.githubusercontent.com/synnyyy/Obsidian/refs/heads/main/addons")Libraries.Library:CheckAssetsFolder()Libraries.Library:LoadModules()Libraries.ThemeManager:SetLibrary(Libraries.Library)Libraries.ThemeManager:SetFolder("Rift")Libraries.ThemeManager:LoadDefault()Libraries.Library:SetNotifySide("left")local Window=Libraries.Library:CreateChangableWindow({Title="Rift",Footer="Loader · "..Libraries.Information.VERSION.." · vaultcord.win/synergy",Icon=Libraries.Library:GetAsset("Logo.png"),Modal=true})local ErrorFrame=Window:CreateCanvas()ErrorFrame:AddLabel("We have changed our loadstring. Click 'Copy' to copy the loadstring. After that, this prompt will be removed after 10 seconds. \n\nStick to the new loadstring instead.\n",true)ErrorFrame:AddButton("Copy",function()setclipboard([[loadstring(game:HttpGet("https://rifton.top/loader.lua"))()]])Libraries.Library:Notify({Description="Copied to clipboard. This prompt will close in 10 seconds.",Time=10})task.wait(10)getgenv().Library:Unload()getgenv().Library=nil end)
